document.addEventListener("DOMContentLoaded",()=>{const d=document.querySelector("header");d&&window.addEventListener("scroll",()=>{d.classList.toggle("scrolled",window.scrollY>40)});const c=document.querySelectorAll(".product-item"),i=document.getElementById("product-main-image");if(c.length&&i){let e=0,r;const l=o=>{c.forEach(L=>L.classList.remove("active"));const n=c[o];n.classList.add("active"),i.src=n.getAttribute("data-image"),e=o},t=()=>{r=setInterval(()=>{l((e+1)%c.length)},4e3)};c.forEach((o,n)=>{o.addEventListener("click",()=>{clearInterval(r),l(n),t()})}),t()}const m=document.querySelector(".carousel-track"),a=document.querySelectorAll(".carousel-dots .dot");if(m&&a.length){let e=0;const r=a.length,l=t=>{m.style.transform=`translateX(-${t*100}%)`,a.forEach(o=>o.classList.remove("active")),a[t].classList.add("active"),e=t};a.forEach((t,o)=>{t.addEventListener("click",()=>l(o))}),setInterval(()=>{l((e+1)%r)},4e3)}const s=document.getElementById("promo-modal"),u=s?.querySelector(".modal-close"),v=s?.querySelector(".modal-overlay");if(s){setTimeout(()=>{s.classList.add("active")},3e3);const e=()=>s.classList.remove("active");u?.addEventListener("click",e),v?.addEventListener("click",e)}});
