document.addEventListener("DOMContentLoaded",()=>{const d=document.querySelector("header");d&&window.addEventListener("scroll",()=>{d.classList.toggle("scrolled",window.scrollY>40)});const s=document.querySelectorAll(".product-item"),i=document.getElementById("product-main-image");if(s.length&&i){let t=0,o;const c=e=>{s.forEach(u=>u.classList.remove("active"));const a=s[e];a.classList.add("active"),i.src=a.dataset.image,t=e},l=()=>{o=setInterval(()=>{c((t+1)%s.length)},4e3)};s.forEach((e,a)=>{e.addEventListener("click",()=>{clearInterval(o),c(a),l()})}),l()}const m=document.querySelector(".carousel-track"),r=document.querySelectorAll(".carousel-dots .dot");if(m&&r.length){let t=0,o=null;const c=r.length,l=e=>{m.style.transform=`translateX(-${e*100}%)`,r.forEach(a=>a.classList.remove("active")),r[e].classList.add("active"),t=e};r.forEach((e,a)=>{e.addEventListener("click",()=>{clearInterval(o),l(a),o=setInterval(()=>l((t+1)%c),4e3)})}),o=setInterval(()=>l((t+1)%c),4e3)}const n=document.getElementById("promo-modal");if(n){const t=n.querySelector(".modal-close"),o=n.querySelector(".modal-overlay"),c=()=>n.classList.remove("active");setTimeout(()=>n.classList.add("active"),3e3),t?.addEventListener("click",c),o?.addEventListener("click",c)}});
